void NumericSensor::updateThresholds()
{
    if (!valueIntf)
    {
        lg2::error(
            "Failed to update thresholds sensor {NAME} D-Bus interfaces don't exist.",
            "NAME", sensorName);
        return;
    }
    auto value = valueIntf->value();

    if (thresholdWarningIntf)
    {
        updateThreshold(thresholdWarningIntf, value, true, hysteresis); // High warning
        updateThreshold(thresholdWarningIntf, value, false, hysteresis); // Low warning
    }

    if (thresholdCriticalIntf)
    {
        updateThreshold(thresholdCriticalIntf, value, true, hysteresis); // High critical
        updateThreshold(thresholdCriticalIntf, value, false, hysteresis); // Low critical
    }
}

