Perhaps switch to this:
    auto respdata =
        reinterpret_cast<const unsigned char*>(nla_data(tb[NCSI_ATTR_DATA]));
    auto ncsiVer = std::span<const unsigned char>(respdata + NCSIVERSN_OFFSET, 4);
    lg2::info("NCSI version (BCD): {NCSI_VERSION}", "NCSI_VERSION",
              toHexStr(ncsiVer));

